import "./App.css";
import React, { Component } from "react";
import axios from "axios";


const options = {
  method: 'POST',
  url: 'https://lastfmdimashirokovv1.p.rapidapi.com/getTopTracksChart',
  headers: {
    'content-type': 'application/x-www-form-urlencoded',
    'x-rapidapi-key': 'd257bc4125msh092415e720b73f8p10e3a0jsn9d1dde59496a',
    'x-rapidapi-host': 'LastFmdimashirokovV1.p.rapidapi.com'
  },
  data: {apiKey: ''}
};

axios.request(options).then(function (response) {
	console.log(response.data);
}).catch(function (error) {
	console.error(error);
});

const topTracksApi = axios.create({
  baseURL:
    "http://ws.audioscrobbler.com/2.0/?method=chart.gettoptracks&api_key=3c49d63630980dac550d6ffd238fa4a3&format=json",
});

class App extends Component {
  state = {
    topTracks: [],
  };

  constructor() {
    super();
    this.getTopTracks();
  }

  getTopTracks = async () => {
    let data = await topTracksApi.get("/").then(({ data }) => data);
    this.setState({ topTracks: data });
  };

  createTopTrack = async () => {
    let res = topTracksApi.post("/", {
      name: "test",
      artist: 4,
      author: "test",
    });
    console.log(res);
    this.getTopTracks();
  };

  deleteTrack = async (id) => {
    let data = await topTracksApi.delete(`/${id}`);
    this.getTopTracks();
  };

  render() {
    return (
      <div className="App">
        <button onClick={this.createTopTrack}>createTopTrack</button>
        {this.state.topTracks.map((topTrack) => (
          <h2 key={topTrack.id}>
            {topTrack.title}
            <button onClick={() => this.deleteTrack(topTrack.id)}>x</button>
          </h2>
        ))}
      </div>
    );
  }
}
export default App;
