import "./App.css";
import React, { Component } from "react";
import axios from "axios";

const topTracksApi = axios.create({
  baseURL:
    "http://ws.audioscrobbler.com/2.0/?method=chart.gettoptracks&api_key=3c49d63630980dac550d6ffd238fa4a3&format=json",
});

class App extends Component {
  state = {
    topTracks: []
  };

  constructor() {
    super();
  }

  getTopTracks= async() => {
    topTracksApi.get("/").then((res) => {
      console.log(res.data);
      this.setState({ topTracks: res.data })
    });
  }

  createTopTrack = async () => {
    let res = topTracksApi.post("/", {title: "test", id: 4, author: "test"})
    console.log(res)
  }

  render() {
    return <div className="App">
      <button onClick={this.createTopTrack}>createTopTrack</button>
      {this.state.topTracks.map(topTrack => <h2 key={topTrack.id}>{topTrack.title}</h2>)}
    </div>
  }
}
export default App;
